    var min = 0
    var max = n
    var sum = 0
    var count = 0
    while (min <= max) {
        var mid = (min + max) / 2
        sum = mid * mid
        count = count + 1
        if (sum == n) {
            return count
        } else if (sum < n) {
            min = mid + 1
        } else {
            max = mid - 1
        }
    }
    return count
}

