    // 1. define 2 variables
    var count = 0
    var total = 0

    // 2. define recursive function
    fun _helper(m : Int, total : Int) {
        // when m = 1, (1+1 + 1) = 2 is the final case
        if (m === 1) {
            count++
            return
        }

        if (total + 1 <= m) {
            _helper(m - 1, total + 1)
        }

        if (total - 1 >= 0) {
            _helper(m - 1, total - 1)
        }
    }

    _helper(m, 0)

    // 3. return your count
    return count
}

