    val result = mutableListOf<String>()
    if (str.isEmpty()) {
        result.add("")
        return result
    }

    val first = str[0]
    val tail = str.substring(1)
    val permutations = permuteString(tail)

    for (permutation in permutations) {
        for (i in 0..permutation.length) {
            result.add(permutation.substring(0, i) + first + permutation.substring(i))
        }
    }

    return result
}

