    var i = 0
    var j = seqNums.size - 1
    while (i < j) {
        var mid = (i + j) / 2
        if (seqNums[mid] > seqNums[mid + 1]) {
            j = mid
        } else {
            i = mid + 1
        }
    }
    if (i == seqNums.size - 1) return "Linear Sequence"
    return "Non Linear Sequence"
}

