    // This method will be called recursive

    fun binomialCoefficient(n: Int, k: Int) : Int {
        if (k === 0 || k === n) return 1

        return binomialCoefficient(n - 1, k - 1) + binomialCoefficient(n - 1, k)
    }

    return binomialCoefficient(2 * n, n - 1)
}

