    /**
     * Write a python function to find the first digit in factorial of a given number.
     * >>> first_Digit(5)
     * 1
     * >>> first_Digit(10)
     * 3
     * >>> first_Digit(7)
     * 5
     */
    var fact = 1
    for (i in 1..n) {
        fact *= i
        while (fact % 10 == 0) {
            fact = fact / 10
        }
    }
    while (fact >= 10) {
        fact = fact / 10
    }
    return fact
}

