canonical_solution:
    $queue = $nums;
    if ($nums > $n) {
        $queue = $nums;
    }
    $i = 0;
    $j = 0;
    $max = $nums[$n - 1];
    while ($queue[$i] != $max) {
        $j = $i;
        while ($queue[$i] != $max && $queue[$i] > $i) {
            $i++;
        }
        if ($i == $j) {
            return false;
        }
        $queue[$j] = $queue[$i];
    }
    $queue[$j] = $max;
    return true;
}

