    while ($i < $n) {
        $queue[$i] = $nums[$i];
        $i++;
    }
    $i = 0;
    while ($i < $n) {
        // Add smallest element to end of queue.
        if ($queue[$i] < $queue[$n - 1]) {
            // Swap the smallest element with element at the end of the queue.
            $queue[$i] = $queue[$n - 1];
            $queue[$n - 1] = $nums[$i];
            $i++;
        } else {
            $n--;
        }
    }
    return $queue;
}

