    $i = 0;
    foreach ($data as $item) {
        $sum += $item;
    }
    $mean = $sum / count($data);
    $sd = 0;
    $i = 0;
    foreach ($data as $item) {
        $diff = $item - $mean;
        $sd += $diff * $diff;
        $i++;
    }
    $sd = sqrt($sd / ($i - 1));
    return $sd;
}

